import React, { useState, useEffect } from "react";
import UsersGrid from "../components/users/index";

export const UsersContainer = () => {
  const [users, setUsers] = useState([]);
  const [planets, setPlanets] = useState([]);

  const getUsers = async () => {
    try {
      let allUsers = [];
      let response = await fetch("https://swapi.dev/api/people");
      let jsonData = await response.json();
      /*
        push users to array
        while there is a next
        fetch llink 
        push users
        repeat...
        */
      while(true){
        jsonData.results.map(result => {
            return allUsers.push(result);
        });
        if(jsonData.next){
            response = await fetch(jsonData.next);
            jsonData = await response.json();
        }else{
            break;
        }
      }  

      console.log(allUsers);
      setUsers(allUsers);
    } catch (error) {
      console.error(error.message);
    }
  };

  const getPlanets= async () => {
    try {
      let allPlanets = [];
      let response = await fetch("https://swapi.dev/api/planets");
      let jsonData = await response.json();

      //push planets to array while there is a next llink push planets repeat...
      while(true){
        jsonData.results.map(result => {
            return allPlanets.push(result);
        });
        if(jsonData.next){
            response = await fetch(jsonData.next);
            jsonData = await response.json();
        }else{
            break;
        }
      }  

      console.log(allPlanets);
      setPlanets(allPlanets);
    } catch (error) {
      console.error(error.message);
    }
  };

  useEffect(() => {
    getUsers();
    getPlanets();
  }, []);

  return (
    <UsersGrid>
      <UsersGrid.UserRow />
    </UsersGrid>
  );
};
